
public class Pattern4 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n=5;
		 int row=1;
		 int nst=n;
//		 int nsp=1;
		 while(row<=n) {
			 int csp=1;
			 int nsp=1;

			 while(cst<=nst) {
				 System.out.print(" * ");
				 cst++;
			 }
			 nst--;
			 row++;
			 System.out.println();
		 }
	}
}



