
public class PatternFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=7;
		int i;
		int j;
		for(i=1;i<=row;i++) {
			for(j=row;j>=i;j--) {
//				System.out.println("");
				System.out.print("*");
//				System.out.println(" ");
//				System.out.println();
				 
			}
			System.out.println(); 
		}

	}

}

